import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export const CreateQuiz = () => {
  const [quiz, setQuiz] = useState({
    title: "",
    description: "",
    questions: [],
  });

  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setQuiz({ ...quiz, [name]: value });
  };

  const handleQuestionChange = (event, index) => {
    const { value } = event.target;
    const questions = quiz.questions;
    questions[index] = value;
    setQuiz({ ...quiz, questions });
  };

  const addQuestion = () => {
    setQuiz({ ...quiz, questions: [...quiz.questions, ""] });
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post("http://localhost:4444/quiz/", quiz);
      alert("Recipe Created!");
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="create-quiz">
      <h2>Create Your Quiz</h2>

      <form onSubmit={onSubmit}>
        <label htmlFor="title">Title</label>
        <input type="text" id="title" name="title" onChange={handleChange} />

        <label htmlFor="description">Description</label>
        <textarea
          name="description"
          id="description"
          onChange={handleChange}
        ></textarea>

        <div className="questions-group">
          {quiz.questions.map((question, index) => {
            return (
              <>
                <label htmlFor={`question-${index}`}>
                  <strong>Question № {index + 1}</strong>
                </label>
                <input
                  key={index}
                  type="text"
                  name={`question-${index}`}
                  value={question}
                  onChange={(event) => handleQuestionChange(event, index)}
                />

                <div className="answer-group">
                  <label htmlFor={`${index}-1`}>
                    <i>Answer 1</i>
                  </label>
                  <input
                    id={`${index}-1`}
                    key={`${index}-1`}
                    type="text"
                    name={`question-${index}-1`}
                    value={question}
                    onChange={(event) => handleQuestionChange(event, index)}
                  />
                </div>

                <label htmlFor={`${index}-2`}>
                  <i>Answer 2</i>
                </label>
                <input
                  key={`${index}-2`}
                  id={`${index}-2`}
                  type="text"
                  name={`question-${index}-2`}
                  value={question}
                  onChange={(event) => handleQuestionChange(event, index)}
                />

                <label htmlFor={`${index}-3`}>
                  <i>Answer 3</i>
                </label>
                <input
                  key={`${index}-3`}
                  id={`${index}-3`}
                  type="text"
                  name={`question-${index}-3`}
                  value={question}
                  onChange={(event) => handleQuestionChange(event, index)}
                />

                <label htmlFor={`${index}-4`}>
                  <i>Answer 4</i>
                </label>
                <input
                  key={`${index}-4`}
                  id={`${index}-4`}
                  type="text"
                  name={`question-${index}-4`}
                  value={question}
                  onChange={(event) => handleQuestionChange(event, index)}
                />
              </>
            );
          })}
          <button
            className="add-question-btn"
            type="button"
            onClick={addQuestion}
          >
            Add Question
          </button>
        </div>
        <button className="submit-btn" type="submit">
          Create
        </button>
      </form>
    </div>
  );
};
