import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export const CreateQuiz = () => {
  /* const [quiz, setQuiz] = useState({
    title: "",
    description: "",
    questions: {
      question: "",
      answers: [],
      correct: 0,
    },
  }); */
  const [answers, setAnswers] = useState(["", "", "", ""]);

  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setQuiz({ ...quiz, [name]: value });
  };

  const handleQuestionChange = (event, index) => {
    const { value } = event.target;
    const questions = quiz.questions;
    questions[index] = value;
    setQuiz({ ...quiz, questions });
  };

  const handleAnswerChange = (event, index) => {
    const { value } = event.target;
    answers[index] = value;
    console.log(answers);
    let ans = [...answers];
    console.log(ans);
  };

  const addQuestion = () => {
    setQuiz({ ...quiz, questions: [...quiz.questions, ""] });
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post("http://localhost:4444/quiz/", quiz);
      alert("Recipe Created!");
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="create-quiz">
      <h2>Create Your Quiz</h2>

      <form onSubmit={onSubmit}>
        <label htmlFor="title">Title</label>
        <input type="text" id="title" name="title" onChange={handleChange} />

        <label htmlFor="description">Description</label>
        <textarea
          name="description"
          id="description"
          onChange={handleChange}
        ></textarea>

        <div className="questions-group">
          {quiz.questions.map((question, index) => {
            return (
              <>
                <label htmlFor={`question-${index}`}>
                  <strong>Question â„– {index + 1}</strong>
                </label>
                <input
                  key={index}
                  type="text"
                  name={`question-${index}`}
                  value={question}
                  onChange={(event) => handleQuestionChange(event, index)}
                />

                <div className="answers-group">
                  <input
                    type="radio"
                    id="answer"
                    name="answer"
                    value="1"
                  ></input>

                  <label htmlFor="answer-1">
                    <i>Answer 1</i>
                  </label>

                  <input
                    id="answer-1"
                    key="answer-1"
                    type="text"
                    name="answer-1"
                    value={answers[0]}
                    onChange={(event) => handleAnswerChange(event, 0)}
                  />
                </div>
                <div className="answers-group">
                  <input
                    type="radio"
                    id="answer"
                    name="answer"
                    value="1"
                  ></input>

                  <label htmlFor="answer-2">
                    <i>Answer 1</i>
                  </label>

                  <input
                    id="answer-2"
                    key="answer-2"
                    type="text"
                    name="answer-2"
                    value={answers[1]}
                    onChange={(event) => handleAnswerChange(event, 1)}
                  />
                </div>
              </>
            );
          })}
          <button
            className="add-question-btn"
            type="button"
            onClick={addQuestion}
          >
            Add Question
          </button>
        </div>
        <button className="submit-btn" type="submit">
          Create
        </button>
      </form>
    </div>
  );
};
